mapper.insert:
insert into board_tb
values( #{board_seq}, #{board_division}, #{board_notice}, #{board_title}, #{board_content}, #{board_hit}, #{board_use_yn}, #{board_register_id}, now(), #{board_update_id}, now(), #{board_etc}
);
SELECT LAST_INSERT_ID();

update.if:
update board_tb
set board_notice = #{board_notice}, board_title = #{board_title}, board_content = #{board_content}, board_update_id = #{board_update_id}, board_update_date = now()
where board_seq = #{board_seq};

mapper.insert:
